INSERT INTO `tnewslettervorlagestd` VALUES (2,1,'Basic Newsletter deutsch','<img src=\"gfx/NewslettervorlageStd/nl_preview_basic.png\" />','{strip}\r\n{* Basic Newsletter Template *}\r\n{* @author JTL-Software GmbH *}\r\n\r\n{assign var=titleArticles value=\"Aktuelle Angebote im Shop: \"}\r\n{assign var=titleManufacturers value=\"Wir führen Artikel von: \"}\r\n{assign var=titleCategories value=\"Stöbern in Kategorien: \"}\r\n{/strip}<html>\r\n<body leftmargin=\"0\" marginwidth=\"0\" topmargin=\"0\" marginheight=\"0\" offset=\"0\" bgcolor=\'#FBFBFB\' >\r\n\r\n<STYLE>\r\n a {ldelim} color:#0000C6; {rdelim}\r\n a img {ldelim} border: none; {rdelim}\r\n</STYLE>\r\n<table width=\"100%\" cellpadding=\"10\" cellspacing=\"0\" class=\"backgroundTable\" bgcolor=\'#FBFBFB\' >\r\n<tr>\r\n<td valign=\"top\" align=\"center\">\r\n\r\n<table width=\"550\" style=\"width: 550px;\" cellpadding=\"0\" cellspacing=\"0\">\r\n\r\n<tr>\r\n<td style=\"background-color:#F6F6F6;border-top:0px solid #000000;border-bottom:1px solid #FFFFFF;text-align:center;padding:0;\" align=\"center\"><span style=\"font-size:20px;color:#333;line-height:200%;font-family:verdana;text-decoration:none;\">$#Banner550pxWide#$</span></td>\r\n</tr>\r\n\r\n</table>\r\n\r\n\r\n<table style=\"width: 550px;\" width=\"550\" cellpadding=\"10\" cellspacing=\"0\" bgcolor=\"#FFFFFF\">\r\n\r\n<tr>\r\n<td bgcolor=\"#FFFFFF\" valign=\"top\" style=\"padding:10px;font-size:12px;color:#000000;line-height:150%;font-family:trebuchet ms;\">\r\n<p>\r\n{* --- Kunden-Anrede --- <!-- *}\r\n{strip}\r\n{if !$Kunde->cNachname}{* --- Allgemeine Anrede, wenn Nachname unbekannt --- *}\r\n  Lieber Kunde, \r\n{else}{* --- Persoenliche Anrede, z.B. \"Lieber Max Mustermann\", \"Lieber Herr Mustermann\" --- *}\r\n   {if $Kunde->cAnrede eq \"w\"}Liebe{else}Lieber{/if} {if $Kunde->cVorname}{$Kunde->cVorname}{else}{$Kunde->cAnredeLocalized}{/if} {$Kunde->cNachname}, \r\n{/if}{/strip}\r\n{* --- /Kunden-Anrede --- --> *}\r\n</p>\r\n\r\n{* --- Newsletter-Inhalt --- <!-- *}\r\n$#TextContent#$\r\n{* --- /Newsletter-Inhalt --- --> *}\r\n\r\n{if $Artikelliste}\r\n<hr style=\"background: #eee; border: transparent none; height: 1px;margin:16px 0;\" />\r\n<strong>{$titleArticles}</strong><br />\r\n{* --- Artikelliste --- <!-- *}\r\n<table style=\"font-size:12px\" cellpadding=\"5\" cellspacing=\"0\"><tr>\r\n{foreach name=artikelliste from=$Artikelliste item=Artikel} \r\n{if $smarty.foreach.artikelliste.index % 3 ==0 && !$smarty.foreach.artikelliste.first}</tr><tr>{/if}\r\n<td valign=\"top\" align=\"center\" style=\"text-align: center;padding-bottom:5px\">\r\n   <a href=\"{$Artikel->cURL}\">{strip}\r\n        <img style=\"border: 0\" src=\"{$Artikel->cVorschaubild}\" alt=\"{$Artikel->cName|strip_tags|escape:\"quotes\"|escape:\"html\"|truncate:60}\" />\r\n        <br />\r\n      <strong>{$Artikel->cName}</strong>\r\n  {/strip}</a>\r\n    {if $Artikel->Preise->fVKNetto==0}\r\n      Preis auf Anfrage\r\n   {else}\r\n      {if $Artikel->nVariationsAufpreisVorhanden == 1}ab {elseif $Artikel->Preise->rabatt>0}jetzt nur {else}nur {/if}\r\n     {$Artikel->Preise->cVKLocalized[$NettoPreise]}\r\n  {/if}\r\n</td>\r\n{if $smarty.foreach.artikelliste.last}</tr>{/if}\r\n{/foreach}\r\n</table>\r\n{* --- /Artikelliste --- --> *}\r\n{/if}\r\n\r\n\r\n{if $Herstellerliste}\r\n<hr style=\"background: #eee; border: transparent none; height: 1px;margin:16px 0;\" />\r\n<strong>{$titleManufacturers}</strong>\r\n{* --- Herstellerliste --- <!-- *}\r\n<table style=\"font-size:12px\" cellpadding=\"5\" cellspacing=\"0\"><tr>\r\n{strip}{foreach name=herstellerliste from=$Herstellerliste item=oHersteller}\r\n{if $smarty.foreach.herstellerliste.index % 3 ==0 && !$smarty.foreach.herstellerliste.first}</tr><tr>{/if}\r\n<td valign=\"top\" align=\"center\" style=\"text-align: center;padding-bottom:5px\">\r\n<a href=\"{$oHersteller->cURL}\">\r\n<img style=\"border: 0\" src=\"{$oHersteller->cBildpfadNormal}\" alt=\"{$oHersteller->cName|strip_tags|escape:\"quotes\"|escape:\"html\"|truncate:60}\" />\r\n<br />\r\n{$oHersteller->cName}\r\n</a>\r\n</td>\r\n{if $smarty.foreach.herstellerliste.last}</tr>{/if}\r\n{/foreach}{/strip}\r\n</table>\r\n{* --- /Herstellerliste --- --> *}\r\n{/if}\r\n\r\n{if $Kategorieliste}\r\n<hr style=\"background: #eee; border: transparent none; height: 1px;margin:16px 0;\" />\r\n<strong>{$titleCategories}</strong>\r\n{* --- Kategorieliste --- <!-- *}\r\n<table style=\"font-size:12px\" cellpadding=\"5\" cellspacing=\"0\"><tr>\r\n{strip}{foreach name=kategorieliste from=$Kategorieliste item=oKategorie}\r\n{if $smarty.foreach.kategorieliste.index % 3 ==0 && !$smarty.foreach.kategorieliste.first}</tr><tr>{/if}\r\n<td valign=\"top\" align=\"center\" style=\"text-align: center;padding-bottom:5px\">\r\n<a href=\"{$oKategorie->cURL}\" title=\"{$oKategorie->cName}\">\r\n<img style=\"border: 0\" src=\"{$oKategorie->cBild}\" alt=\"{$oKategorie->cName|strip_tags|escape:\"quotes\"|escape:\"html\"|truncate:60}\" />\r\n<br />\r\n{$oKategorie->cName}\r\n</a>\r\n</td>\r\n{if $smarty.foreach.kategorieliste.last}</tr>{/if}\r\n{/foreach}{/strip}\r\n</table>\r\n{* --- /Kategorieliste --- --> *}\r\n{/if}\r\n\r\n<p style=\"text-align: right; clear: both;\"><a href=\"#top\">^ nach oben</a></p>\r\n\r\n</td>\r\n</tr>\r\n\r\n<tr>\r\n<td style=\"padding:10px;background-color:#F6F6F6;border-top:10px solid #FFFFFF;font-family:trebuchet ms;font-size:12px;color:#666;\" valign=\"top\">\r\n<center>$#TextFooter#$<center>\r\n</td>\r\n</tr>\r\n\r\n{*********************************** To Do ******************************\r\n<tr>\r\n<td style=\"background-color:#eee;font-family:trebuchet ms;font-size:12px;color:#666;padding: 5px;margin-top:1px;\" valign=\"top\">\r\n<center>Wird diese E-Mail nicht richtig dargestellt? <a href=\"{$URL_SHOP}\" style=\"text-decoration:none;\">&raquo; Newsletter im Browser öffnen.</a></center>\r\n</td>\r\n</tr>\r\n*************************************************************************}\r\n</table>\r\n\r\n\r\n\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n\r\n\r\n</body>\r\n</html>','{strip}\r\n{* Basic Newsletter Template *}\r\n{* @author JTL-Software GmbH *}\r\n\r\n{assign var=titleArticles value=\"Aktuelle Angebote im Shop: \"}\r\n{assign var=titleManufacturers value=\"Wir führen Artikel von: \"}\r\n{assign var=titleCategories value=\"Stöbern in Kategorien: \"}\r\n{/strip}{strip}{if !$Kunde->cNachname}{* --- Allgemeine Anrede, wenn Nachname unbekannt --- *}\r\nLieber Kunde, \r\n{else}{* --- Persoenliche Anrede, z.B. \"Lieber Max Mustermann\", \"Lieber Herr Mustermann\" --- *}\r\n{if $Kunde->cAnrede eq \"w\"}Liebe{else}Lieber{/if} {if $Kunde->cVorname}{$Kunde->cVorname}{else}{$Kunde->cAnredeLocalized}{/if} {$Kunde->cNachname}, \r\n{/if}{/strip}\r\n\r\n$#TextContent#$\r\n{if $Artikelliste}\r\n\r\n---\r\n\r\n\r\n{$titleArticles}\r\n{foreach name=artikelliste from=$Artikelliste item=Artikel} \r\n{$Artikel->cName} {if $Artikel->Preise->fVKNetto==0}(Preis auf Anfrage){else}{if $Artikel->nVariationsAufpreisVorhanden == 1}ab {elseif $Artikel->Preise->rabatt>0}jetzt nur {else}nur {/if}{$Artikel->Preise->cVKLocalized[$NettoPreise]}{/if} -> {$Artikel->cURL}\r\n{/foreach}\r\n{/if}\r\n{if $Herstellerliste}\r\n\r\n---\r\n\r\n{$titleManufacturers}\r\n\r\n{foreach name=herstellerliste from=$Herstellerliste item=oHersteller} \r\n{$oHersteller->cName} -> {$oHersteller->cURL}\r\n{/foreach}{/if}\r\n{if $Kategorieliste}\r\n\r\n---\r\n\r\n{$titleCategories}\r\n\r\n{foreach name=kategorieliste from=$Kategorieliste item=oKategorie}\r\n{$oKategorie->cName} -> {$oKategorie->cURL}\r\n{/foreach}{/if}\r\n\r\n---\r\n\r\n$#TextFooter#$'),(3,1,'Newsletter 2spaltig A deutsch','<img src=\"gfx/NewslettervorlageStd/nl_preview_2col_right.png\" />','{strip}\r\n{* Basic Newsletter Template *}\r\n{* @author JTL-Software GmbH *}\r\n\r\n{assign var=titleArticles value=\"Aktuelle Angebote im Shop: \"}\r\n{assign var=titleManufacturers value=\"Wir führen Artikel von: \"}\r\n{assign var=titleCategories value=\"Stöbern in